    const scrollContainer = ref(null);

    const selectCard = (id) => {
    selectedCard.value = id;
    };

    watch(selectedCard, (newId) => {
    const cardElement = cardRefs.value[newId];
    if (cardElement && scrollContainer.value) {
        const containerRect = scrollContainer.value.getBoundingClientRect();
        const cardRect = cardElement.getBoundingClientRect();
        const offset = cardRect.left - containerRect.left + scrollContainer.value.scrollLeft - (containerRect.width / 2 - cardRect.width / 2);

        scrollContainer.value.scrollTo({
        left: offset,
        behavior: 'smooth',
        });
    }
    });


